/*Table structure for table `tb_refund` */

DROP TABLE IF EXISTS `tb_refund`;

CREATE TABLE `tb_refund` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL COMMENT '用户ID',
  `order_id` int(11) DEFAULT NULL COMMENT '订单ID',
  `order_no` varchar(50) DEFAULT NULL COMMENT '商户订单号',
  `refund_type` int(2) DEFAULT NULL COMMENT '1:会费',
  `result_code` varchar(16) DEFAULT NULL COMMENT 'SUCCESS/FAIL SUCCESS退款申请接收成功，结果通过退款查询接口查询 FAIL 提交业务失败',
  `err_code` varchar(32) DEFAULT NULL COMMENT '错误码',
  `err_code_des` varchar(128) DEFAULT NULL COMMENT '结果信息描述',
  `out_trade_no` varchar(32) DEFAULT NULL COMMENT '商户系统内部的订单号',
  `transaction_id` varchar(28) DEFAULT NULL COMMENT '微信订单号',
  `out_refund_no` varchar(32) DEFAULT NULL COMMENT '商户退款单号',
  `refund_id` varchar(28) DEFAULT NULL COMMENT '微信退款单号',
  `refund_channel` varchar(16) DEFAULT NULL COMMENT 'ORIGINAL—原路退款 BALANCE—退回到余额',
  `refund_fee` int(11) DEFAULT NULL COMMENT '退款总金额,单位为分,可以做部分退款',
  `settlement_refund_fee` int(11) DEFAULT NULL COMMENT '去掉非充值代金券退款金额后的退款金额，退款金额=申请退款金额-非充值代金券退款金额，退款金额<=申请退款金额',
  `total_fee` int(11) DEFAULT NULL COMMENT '订单总金额，单位为分，只能为整数',
  `settlement_total_fee` int(11) DEFAULT NULL COMMENT '去掉非充值代金券金额后的订单总金额，应结订单金额=订单金额-非充值代金券金额，应结订单金额<=订单金额。',
  `cash_fee` int(11) DEFAULT NULL COMMENT '现金支付金额，单位为分，只能为整数',
  `cash_refund_fee` int(11) DEFAULT NULL COMMENT '现金退款金额，单位为分，只能为整数',
  `coupon_type_$n` varchar(8) DEFAULT NULL COMMENT 'CASH--充值代金券 NO_CASH---非充值代金券 订单使用代金券时有返回（取值：CASH、NO_CASH）。$n为下标,从0开始编号',
  `coupon_refund_fee_$n` int(11) DEFAULT NULL COMMENT '代金券退款金额<=退款金额，退款金额-代金券或立减优惠退款金额为现金',
  `coupon_refund_count_$n` int(11) DEFAULT NULL COMMENT '退款代金券使用数量 ,$n为下标,从0开始编号',
  `coupon_refund_batch_id_$n_$m` varchar(20) DEFAULT NULL COMMENT '退款代金券批次ID ,$n为下标，$m为下标，从0开始编号',
  `coupon_refund_id_$n_$m` varchar(20) DEFAULT NULL COMMENT '退款代金券ID, $n为下标，$m为下标，从0开始编号',
  `coupon_refund_fee_$n_$m` int(11) DEFAULT NULL COMMENT '单个退款代金券支付金额, $n为下标，$m为下标，从0开始编号',
  `create_time` bigint(20) DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  KEY `order_id` (`order_id`),
  CONSTRAINT `tb_refund_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `tb_user_login` (`id`),
  CONSTRAINT `tb_refund_ibfk_2` FOREIGN KEY (`order_id`) REFERENCES `tb_order` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8mb4;

